mapper.select:
SELECT BOARD_TYPE ,BOARD_NUM ,BOARD_TITLE ,BOARD_COMMENT ,TOTAL_CNT ,BOARD_CREATOR FROM ( SELECT code.code_name as BOARD_TYPE ,bd.BOARD_NUM ,bd.BOARD_TITLE ,bd.BOARD_COMMENT ,ROW_NUMBER() OVER(ORDER BY BOARD_NUM DESC) AS NUMROW ,COUNT(*) OVER() AS TOTAL_CNT ,bd.creator as BOARD_CREATOR FROM BOARD bd left JOIN com_code code ON bd.BOARD_TYPE = code.code_id WHERE ( code.code_type = #{codeType} and code.code_id in
#{item}
) ) WHERE NUMROW BETWEEN (#{pageNo}-1)*#{amount} + 1 AND (#{pageNo})*#{amount}

select BOARD_TYPE ,BOARD_NUM ,BOARD_TITLE ,BOARD_COMMENT ,TOTAL_CNT ,BOARD_CREATOR from ( SELECT code.code_name as BOARD_TYPE ,bd.BOARD_NUM ,bd.BOARD_TITLE ,bd.BOARD_COMMENT ,ROW_NUMBER() OVER(ORDER BY BOARD_NUM DESC) AS NUMROW ,COUNT(*) OVER() AS TOTAL_CNT ,bd.creator as BOARD_CREATOR FROM BOARD bd left JOIN com_code code ON bd.BOARD_TYPE = code.code_id where
code.code_id = 'a01'
code.code_id = 'a02'
code.code_id = 'a03'
code.code_id = 'a04'
) WHERE NUMROW BETWEEN (#{pageNo}-1)*#{amount} + 1 AND (#{pageNo})*#{amount}

mapper.update:
UPDATE BOARD
SET
BOARD_TITLE = #{boardTitle},
BOARD_COMMENT = #{boardComment}
where
BOARD_TYPE = #{boardType}
AND BOARD_NUM = #{boardNum}

mapper.delete:
DELETE FROM BOARD
where
BOARD_TYPE = #{boardType}
AND BOARD_NUM = #{boardNum}
